{"title":"Word_Cloud_Beta","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "description": "A word cloud visualization depicting text data from Elasticsearch.",
  "autosize": "none",
  "width": 1000,
  "height": 800,
  "padding": 100,

  "data": [
    {
      "name": "table",
      "url": {
         // %context%: true
         // %timefield%: @timestamp
        // Define your Elasticsearch URL and query here
        "index": "_all",
        "body": {
          "size": 2,
          "_source": ["highest_topic", "@timestamp"]
        }
      },
      "format": {"property": "hits.hits"},
      "transform": [
        {
          "type": "formula", "as": "angle",
          "expr": "[-45, 0, 45][~~(random() * 3)]"
        },
        {
          "type": "formula", "as": "weight",
          "expr": "if(datum.content=='VEGA', 600, 300)"
        }
      ]
    }
  ],
  "scales": [
    {
      "name": "color",
      "type": "ordinal",
      "domain": {"data": "table", "field": "_source.highest_topic"},
      "range": ["#d5a928", "#652c90", "#939597"]
    }
  ],

  "marks": [
    {
      "type": "text",
      "from": {"data": "table"},
      "encode": {
        "enter": {
          "text": {"field": "_source.highest_topic"},
          "align": {"value": "center"},
          "baseline": {"value": "alphabetic"},
          "fill": {"scale": "color", "field": "_source.highest_topic"} // Use a single color or a specific color for the word cloud
        },
        "update": {
          "fillOpacity": {"value": 1}
        },
        "hover": {
          "fillOpacity": {"value": 0.5}
        },
      "transform": [
          {
            "type": "wordcloud",
            "size": [800, 400],
            "text": {"field": "_source.highest_topic"},
            //"rotate": {"field": "datum.angle"},
            "rotate": {"signal": "[-45, 0, 45][~~(random() * 3)]"},
            "font": "Helvetica Neue, Arial",
            "fontSize": {"field": "datum.count"},
            "fontWeight": {"field": "datum.weight"},
            "fontSizeRange": [12, 56],
            "padding": 2
          }
        ]
      }
    }
  ]
}
"}}
